import React, { useContext } from 'react';
import { ScoreContext } from '.';
import { icons } from './data';
import styled from 'styled-components';
import triangle from './images/bg-triangle.svg';

const Btn = styled.button`
    height: 150px;
    width: 150px;
    border-radius: 100%;
    border: none;
    position: relative;


    &:hover {
        cursor: pointer;
    }

    &:after {
        content: '  ';
        position: absolute;
        box-shadow: 0em 0em 1em black;
        border-radius: 100%;
        transform: scale(1.1);
        top: -4px; 
        bottom: -4px;
        left: -4px; 
        right: -4px;
        background: ${props => props.gradient};
        z-index: -1;
    }
`
const ButtonWrap = styled.div`
    display: grid;
    width: 500px;
    grid-template-areas: "left x x right"
                        "x bottom bottom x";
    grid-template-columns: 25% 25% 25% 25%;
    grid-template-rows: 50% 50%;
    grid-rows: 2 / 50%;
    grid-gap: 2em;
`

export default function Game() {
    const { outcome, compChoice, playerChoice, handleChange } = useContext(ScoreContext);

    const buttons = icons.map((icon) => { 
        return <>
            <Btn style={{gridArea: icon.area}} key={icon.name} gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                <img key={icon.name} src={icon.src} />
            </Btn>
        </> 
    })


    return(<>
        <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
            <ButtonWrap>
                {buttons}
            </ButtonWrap>    
        </div>
    </>)
}
