import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Header from './Header';
const root = document.getElementById('root');

function App () { 

    const [outcome, setOutcome] = useState();
    const [random, setRandom] = useState()
    const outcomes = ["rock", "paper", "scissors"];

    const handleClick = () => {
        const randomNumber = Math.floor(Math.random() * outcomes.length);

        if (randomNumber == outcomes.length) {
            return setOutcome(outcomes[0])
        } else {
            return setOutcome(outcomes[randomNumber]);
        }
    }

    return(<>
        <Header />
        <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
            <h1>{outcome}</h1>
            <button onClick={handleClick}>Outcome</button>
        </div>
    </>)
}

ReactDOM.render(<App />, root)
