import React, { useContext } from 'react';
import { ScoreContext } from '.';
import { icons } from './data';
import styled from 'styled-components';
import triangle from './images/bg-triangle.svg';

const Btn = styled.button`
    min-height: 150px;
    min-width: 150px;
    border-radius: 100%;
    border: none;
    position: relative;

    &:hover {
        cursor: pointer;
    }

    &:nth-of-type(even) {
        bottom: 0;
    }

    &:after {
        content: '  ';
        position: absolute;
        border-radius: 100%;
        transform: scale(1.1);
        top: -4px; 
        bottom: -4px;
        left: -4px; 
        right: -4px;
        background: ${props => props.gradient};
        z-index: -1;
    }
`

export default function Game() {
    const { outcome, compChoice, playerChoice, handleChange } = useContext(ScoreContext);

    const buttons = icons.map((icon) => { 
        return <>
            <Btn gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                <img src={icon.src} />
            </Btn>
        </> 
    })

    return(<>
        <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
            <h1>{outcome}</h1>
            <h2>Computer Chose: {compChoice}</h2>
            <h2>Player Chose: {playerChoice}</h2>      
            <div id='button-wrap'>
                {buttons}
                <img src={triangle} id='triangle'/>
            </div>      



        </div>
    </>)
}
