import React, { useContext } from 'react';
import { ScoreContext } from '.';
import { icons } from './data';
import styled from 'styled-components';

const Btn = styled.button`
    height: 150px;
    width: 150px;
    border-radius: 100%;
    position: relative;
`

export default function Game() {
    const { outcome, compChoice, playerChoice, handleChange } = useContext(ScoreContext);

    const buttons = icons.map((icon) => { 
        return <>
            <Btn onClick={() => handleChange(icon.name)}>
                <img src={icon.src} />
            </Btn>
        </> 
    })

    return(<>
        <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
            <h1>{outcome}</h1>
            <h2>Computer Chose: {compChoice}</h2>
            <h2>Player Chose: {playerChoice}</h2>      
            <div id='button-wrap'>
                {buttons}
            </div>          
        </div>
    </>)
}
