import React, { useContext } from 'react';
import { ScoreContext } from '.';
import { icons } from './data';
import styled from 'styled-components';
import triangle from './images/bg-triangle.svg';

const Btn = styled.button`
    min-height: 150px;
    min-width: 150px;
    border-radius: 100%;
    border: none;
    position: relative;

    &:hover {
        cursor: pointer;
    }

    &:after {
        content: '  ';
        position: absolute;
        border-radius: 100%;
        transform: scale(1.1);
        top: -4px; 
        bottom: -4px;
        left: -4px; 
        right: -4px;
        background: ${props => props.gradient};
        z-index: -1;
    }
`

export default function Game() {
    const { outcome, compChoice, playerChoice, handleChange } = useContext(ScoreContext);

    const buttons = icons.map((icon) => { 
        return <>
            <Btn key={icon.name} gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                <img key={icon.name} src={icon.src} />
            </Btn>
        </> 
    })

    console.log(playerChoice, compChoice)

    return(<>
        <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
            <div id='button-wrap'>
                <Btn key={icon.name} gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                    <img key={icon.name} src={icon.src} />
                </Btn>
                <Btn key={icon.name} gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                    <img key={icon.name} src={icon.src} />
                </Btn>
                <Btn key={icon.name} gradient={icon.gradient} onClick={() => handleChange(icon.name)}>
                    <img key={icon.name} src={icon.src} />
                </Btn>
            </div>    
        </div>
    </>)
}
