import React, { useState, createContext, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Header from './Header';

import { randomNumber } from './randomNumber';
import { winningRules } from './winningRules';

export const ScoreContext = createContext(null);

function App () { 
    const outcomes = ['rock', 'paper', 'scissors'];

    const [outcome, setOutcome] = useState();
    const [random, setRandom] = useState(randomNumber(outcomes));

    const [playerScore, setPlayerScore] = useState(0);
    const [compScore, setCompScore] = useState(0);

    const [playerChoice, setPlayerChoice] = useState();
    const [compChoice, setCompChoice] = useState();

    const [bClick, setBClick] = useState(false);

    const handleChange = (input) => {
        setOutcome('Thinking...');
        setRandom(randomNumber(outcomes))
        setCompChoice(outcomes[random]);
        setPlayerChoice(input);
    };

    useEffect(() => {
        let timeout = setTimeout(() => {
            if (winningRules(playerChoice, compChoice) == 'win') {
                setOutcome('Player Wins!')
                setPlayerScore((prevScore) => prevScore + 1)
            } else if (winningRules(playerChoice, compChoice) == 'lose') {
                setOutcome('Computer Wins!')
                setCompScore((prevScore) => prevScore + 1)
            } else if (winningRules(playerChoice, compChoice) == 'draw') {
                setOutcome(`It's a draw...`)
            }
        }, 2000)
    }, [playerChoice])

    return(<>
        <ScoreContext.Provider value={{playerScore, setPlayerScore, compScore, setCompScore}}>
            <Header />
            <div style={{alignSelf: "center", margin: "0 auto", textAlign: "center"}}>
                <h1>{outcome}</h1>
                <h2>Computer Chose: {compChoice}</h2>
                <h2>Player Chose: {playerChoice}</h2>                
                <button onClick={() => {handleChange('rock')}}>Rock</button>
                <button onClick={() => {handleChange('paper')}}>Paper</button>
                <button onClick={() => {handleChange('scissors')}}>Scissors</button>
            </div>
        </ScoreContext.Provider>
    </>)
}

ReactDOM.render(<App />, document.getElementById('root'))
